package com.thrift.zk.soa.utils;import com.google.gson.Gson;/** * Author: baichuan - xiajun * Date: 16/11/01 00:31 */public class JsonUtil {    public static <T> String toString(T obj) {        Gson gson = new Gson();        if(obj == null){            return null;        }        return gson.toJson(obj);    }    public static <T> T parse(String json, Class clazz) {        Gson gson = new Gson();        if(json == null || "".equals(json)){            return null;        }        return (T) gson.fromJson(json, clazz);    }}